    //filterByColor.map((color:any) => console.log(color))
    filters.map((f:any)=>f.options.map((o:any)=>{if(o.value == e.target.value){o.checked = false}}))
    setFilterByColor(filterByColor.filter((item:any) => item !== e.target.value));
  }
  //console.log(e.target.value)

 }

