const AuthReducer=(state:any,action:any)=>{
    switch(action.type){
        case "Login_Start":
            return{
                user_detail:null,
                isFetching:true,
                error:false
            };

        case "Login_Success":
            return{
                user_detail:action.payload,
                isFetching: false,
                error: false
            };

        case "Login_Failure":
            return{
                user_detail:null,
                isFetching:false,
                error: action.payload
            }

        default:
            return state
    }
}

export default AuthReducer
