import { useState } from "react";
import { Link } from "react-router-dom";



function Header() {
    // checks if user exists in local session for logging out
   const checkIsLoggedIn = () => {
        return localStorage.getItem("user") != null;
    };

    const [isLoggedIn, setIsLoggedIn] = useState<boolean>(checkIsLoggedIn);

    // function for logging out
   /* const logout = () => {
        localStorage.removeItem("user");
        setIsLoggedIn(checkIsLoggedIn);
    };*/

    const renderLoginSignup = (
        <><button className="mr-10 bg-transparent text-white font-extrabold text-lg hover:text-black w-32">
            <Link to="/login">Login</Link>
        </button>
            <button className="mr-10 bg-transparent text-white font-extrabold text-lg hover:text-black w-32">
                <Link to="/signup">Signup</Link>
            </button>
        </>
    );

    const renderLogoutCart = (
        <>
            <button className="mr-10 h-10 mt-3 bg-transparent text-white font-extrabold text-lg hover:text-black w-32">
                <Link to="/cart">Cart</Link>
            </button>
            <button className="mr-10 h-10 mt-3 bg-transparent text-white font-extrabold text-lg hover:text-black w-32" >
                Logout
            </button>
        </>
    );

    return (
        <div className="w-full bg-red-700 h-13 flex justify-between font-bold text-lg ">
            <div>
                <button className="bg-white w-52 my-3 ml-4 rounded-2xl text-lg">
                   Group AD Web Store
                </button>
            </div>

        </div>);
}

export default Header;
