import { useEffect, useMemo, useState } from "react";
import "./index.css";
import {
  AiFillQuestionCircle,
  AiFillCloseCircle,
  AiFillClockCircle,
  AiFillCheckCircle,
} from "react-icons/ai";
import axios from "axios";

const products = [
  {
    id: 1,
    name: "Basic Tee",
    href: "#",
    price: "$32.00",
    color: "Sienna",
    inStock: true,
    size: "Large",
    imageSrc:
      "https://tailwindui.com/img/ecommerce-images/shopping-cart-page-01-product-01.jpg",
    imageAlt: "Front of men's Basic Tee in sienna.",
  },
  {
    id: 2,
    name: "Basic Tee",
    href: "#",
    price: "$32.00",
    color: "Black",
    inStock: false,
    leadTime: "3–4 weeks",
    size: "Large",
    imageSrc:
      "https://tailwindui.com/img/ecommerce-images/shopping-cart-page-01-product-02.jpg",
    imageAlt: "Front of men's Basic Tee in black.",
  },
  {
    id: 3,
    name: "Nomad Tumbler",
    href: "#",
    price: "$35.00",
    color: "White",
    inStock: true,
    imageSrc:
      "https://tailwindui.com/img/ecommerce-images/shopping-cart-page-01-product-03.jpg",
    imageAlt: "Insulated bottle with white base and black snap lid.",
  },
];

export default function Cart() {
  const [cart, setCart] = useState<any>([]);
  const [d, setD] = useState<any>([]);
  //  const [total,setTotal] = useState<any>()

  useEffect(() => {
    const FetchUserCart = async () => {
      const res = await axios.get(
        `http://localhost:3333/639f5d2ab447416b982b578a`
      );
      /*res.data.Cart?.map(async(c:any)=>{    
      const d = await axios.get(`http://localhost:3333/product/${c.id}`)
      setCart([...cart,d.data])
    })*/
      //console.log(res.data.Cart)
      setCart(res.data.Cart);

      /*res.data.Cart.map((m:any)=>{


     // console.log(m.name,m.price)
    })*/
    };
    FetchUserCart();
  }, []);

  // console.log(cart);

  const total = useMemo(() => {
    return cart?.reduce(
      (previousScore: any, currentScore: any) =>
        previousScore + currentScore.price * currentScore.quantity,
      0
    );
  }, [cart]);

  const handleQuantityChange = async (e: any, c: any) => {
    console.log("value: ", e.target.value);
    e.preventDefault();
    try {
      await axios.put(
        `http://localhost:3333/cartUpdate/639f5d2ab447416b982b578a/${c.id}/${e.target.value}`
      );
      //console.log(cart.reduce((accumulator:any,current:any) => accumulator + current.price, 0))
      /*const totalPrice = cart.reduce((accumulator:any,current:any) => accumulator + current.price*current.quantity, 0)
    console.log(totalPrice)*/
      /*setTotal(cart.reduce((accumulator:any,current:any) => accumulator + current.price*current.quantity, 0))*/

      setCart(
        cart.map((value: any) => {
          if (value.id === c.id) {
            return {
              ...value,
              quantity: e.target.value,
            };
          }
          return value;
        })
      );

      console.log(
        cart.map((value: any) => {
          if (value.id === c.id) {
            return {
              ...value,
              quantity: e.target.value,
            };
          }
          return value;
        })
      );
      // window.location.reload();
    } catch (err) {}
    // console.log(e.target.value,c)
  };

  //console.log(d)

  /*cart?.map(async(c:any)=>{
    try{
      const res = await axios.get(`http://localhost:3333/product/${c.id}`)
      setD([...d,res.data])
    }
    catch(err){}
  })

console.log(d)*/

  return (
    <div className="bg-white">
      <div className="max-w-2xl mx-auto pt-16 pb-24 px-4 sm:px-6 lg:max-w-7xl lg:px-8">
        <h1 className="text-3xl font-extrabold tracking-tight text-gray-900 sm:text-4xl">
          Shopping Cart
        </h1>
        <form className="mt-12 lg:grid lg:grid-cols-12 lg:gap-x-12 lg:items-start xl:gap-x-16">
          <section aria-labelledby="cart-heading" className="lg:col-span-7">
            <h2 id="cart-heading" className="sr-only">
              Items in your shopping cart
            </h2>

            <ul
              role="list"
              className="border-t border-b border-gray-200 divide-y divide-gray-200"
            >
              {cart?.map((c: any) => (
                <li key={c.id} className="flex py-6 sm:py-10">
                  <div className="flex-shrink-0">
                    <img
                      src={c.img}
                      className="w-24 h-24 rounded-md object-center object-cover sm:w-48 sm:h-48"
                    />
                  </div>

                  <div className="ml-4 flex-1 flex flex-col justify-between sm:ml-6">
                    <div className="relative pr-9 sm:grid sm:grid-cols-2 sm:gap-x-6 sm:pr-0">
                      <div>
                        <div className="flex justify-between">
                          <h3 className="text-sm">
                            <a className="font-medium text-gray-700 hover:text-gray-800">
                              {c.name}
                            </a>
                          </h3>
                        </div>

                        <div className="mt-1 flex text-sm">
                          <p className="text-gray-500">{c.color}</p>
                          {c.size ? (
                            <p className="ml-4 pl-4 border-l border-gray-200 text-gray-500">
                              {c.size}
                            </p>
                          ) : null}
                        </div>

                        <div className="mt-1 flex text-sm">
                          <p className="text-gray-500">{c.brand}</p>
                        </div>
                        <p className="mt-1 text-sm font-medium text-gray-900">
                          $ {c.price}
                        </p>
                      </div>

                      <div className="mt-4 sm:mt-0 sm:pr-9">
                        <label className="sr-only">c.quantity</label>
                        <select
                          value={c.quantity}
                          onChange={(e) => handleQuantityChange(e, c)}
                          /*onChange={async(e)=>{
                          try{
                            await axios.put(`http://localhost:3333/cartUpdate/639f5d2ab447416b982b578a/${c.id}/${e.target.value}`)
                            total = cart.reduce((accumulator:any,current:any) => accumulator + current.price, 0)
                           console.log(total)
                          }
                          catch(err){

                          }
                        }}*/
                          className="max-w-full rounded-md border border-gray-300 py-1.5 text-base leading-5 font-medium text-gray-700 text-left shadow-sm focus:outline-none focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                        >
                          <option value={1}>1</option>
                          <option value={2}>2</option>
                          <option value={3}>3</option>
                          <option value={4}>4</option>
                          <option value={5}>5</option>
                          <option value={6}>6</option>
                          <option value={7}>7</option>
                          <option value={8}>8</option>
                        </select>

                        <div className="absolute top-0 right-0">
                          <button
                            type="button"
                            className="-m-2 p-2 inline-flex text-gray-400 hover:text-gray-500"
                          >
                            <span className="sr-only">Remove</span>
                            <AiFillCloseCircle
                              className="h-5 w-5"
                              aria-hidden="true"
                            />
                          </button>
                        </div>
                      </div>
                    </div>

                    {/*<p className="mt-4 flex text-sm text-gray-700 space-x-2">
                    {product.inStock ? (
                      <AiFillCheckCircle className="flex-shrink-0 h-5 w-5 text-green-500" aria-hidden="true" />
                    ) : (
                      <AiFillClockCircle className="flex-shrink-0 h-5 w-5 text-gray-300" aria-hidden="true" />
                    )}

                    <span>{product.inStock ? 'In stock' : `Ships in ${product.leadTime}`}</span>
                    </p>*/}
                  </div>
                </li>
              ))}

              {products.map((product, productIdx) => (
                <li key={product.id} className="flex py-6 sm:py-10">
                  <div className="flex-shrink-0">
                    <img
                      src={product.imageSrc}
                      alt={product.imageAlt}
                      className="w-24 h-24 rounded-md object-center object-cover sm:w-48 sm:h-48"
                    />
                  </div>

                  <div className="ml-4 flex-1 flex flex-col justify-between sm:ml-6">
                    <div className="relative pr-9 sm:grid sm:grid-cols-2 sm:gap-x-6 sm:pr-0">
                      <div>
                        <div className="flex justify-between">
                          <h3 className="text-sm">
                            <a
                              href={product.href}
                              className="font-medium text-gray-700 hover:text-gray-800"
                            >
                              {product.name}
                            </a>
                          </h3>
                        </div>
                        <div className="mt-1 flex text-sm">
                          <p className="text-gray-500">{product.color}</p>
                          {product.size ? (
                            <p className="ml-4 pl-4 border-l border-gray-200 text-gray-500">
                              {product.size}
                            </p>
                          ) : null}
                        </div>
                        <p className="mt-1 text-sm font-medium text-gray-900">
                          {product.price}
                        </p>
                      </div>

                      <div className="mt-4 sm:mt-0 sm:pr-9">
                        <label
                          htmlFor={`quantity-${productIdx}`}
                          className="sr-only"
                        >
                          Quantity, {product.name}
                        </label>
                        <select
                          //onChange={(e)=>handleQuantityChange(e)}
                          id={`quantity-${productIdx}`}
                          name={`quantity-${productIdx}`}
                          className="max-w-full rounded-md border border-gray-300 py-1.5 text-base leading-5 font-medium text-gray-700 text-left shadow-sm focus:outline-none focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                        >
                          <option value={1}>1</option>
                          <option value={2}>2</option>
                          <option value={3}>3</option>
                          <option value={4}>4</option>
                          <option value={5}>5</option>
                          <option value={6}>6</option>
                          <option value={7}>7</option>
                          <option value={8}>8</option>
                        </select>

                        <div className="absolute top-0 right-0">
                          <button
                            type="button"
                            className="-m-2 p-2 inline-flex text-gray-400 hover:text-gray-500"
                          >
                            <span className="sr-only">Remove</span>
                            <AiFillCloseCircle
                              className="h-5 w-5"
                              aria-hidden="true"
                            />
                          </button>
                        </div>
                      </div>
                    </div>

                    <p className="mt-4 flex text-sm text-gray-700 space-x-2">
                      {product.inStock ? (
                        <AiFillCheckCircle
                          className="flex-shrink-0 h-5 w-5 text-green-500"
                          aria-hidden="true"
                        />
                      ) : (
                        <AiFillClockCircle
                          className="flex-shrink-0 h-5 w-5 text-gray-300"
                          aria-hidden="true"
                        />
                      )}

                      <span>
                        {product.inStock
                          ? "In stock"
                          : `Ships in ${product.leadTime}`}
                      </span>
                    </p>
                  </div>
                </li>
              ))}
            </ul>
          </section>

          {/* Order summary */}
          <section
            aria-labelledby="summary-heading"
            className="mt-16 bg-gray-50 rounded-lg px-4 py-6 sm:p-6 lg:p-8 lg:mt-0 lg:col-span-5"
          >
            <h2
              id="summary-heading"
              className="text-lg font-medium text-gray-900"
            >
              Order summary
            </h2>

            <dl className="mt-6 space-y-4">
              <div className="flex items-center justify-between">
                <dt className="text-sm text-gray-600">Subtotal</dt>
                <dd className="text-sm font-medium text-gray-900">$ {total}</dd>
              </div>
              <div className="border-t border-gray-200 pt-4 flex items-center justify-between">
                <dt className="flex items-center text-sm text-gray-600">
                  <span>Shipping estimate</span>
                  <a
                    href="#"
                    className="ml-2 flex-shrink-0 text-gray-400 hover:text-gray-500"
                  >
                    <span className="sr-only">
                      Learn more about how shipping is calculated
                    </span>
                    <AiFillQuestionCircle
                      className="h-5 w-5"
                      aria-hidden="true"
                    />
                  </a>
                </dt>
                <dd className="text-sm font-medium text-gray-900">$5.00</dd>
              </div>
              <div className="border-t border-gray-200 pt-4 flex items-center justify-between">
                <dt className="flex text-sm text-gray-600">
                  <span>Tax estimate</span>
                  <a
                    href="#"
                    className="ml-2 flex-shrink-0 text-gray-400 hover:text-gray-500"
                  >
                    <span className="sr-only">
                      Learn more about how tax is calculated
                    </span>
                    <AiFillQuestionCircle
                      className="h-5 w-5"
                      aria-hidden="true"
                    />
                  </a>
                </dt>
                <dd className="text-sm font-medium text-gray-900">
                  $ {(total * 13) / 100}
                </dd>
              </div>
              <div className="border-t border-gray-200 pt-4 flex items-center justify-between">
                <dt className="text-base font-medium text-gray-900">
                  Order total
                </dt>
                <dd className="text-base font-medium text-gray-900">
                  ${total + 5 + (total * 13) / 100}
                </dd>
              </div>
            </dl>

            <div className="mt-6">
              <button
                type="submit"
                className="w-full bg-indigo-600 border border-transparent rounded-md shadow-sm py-3 px-4 text-base font-medium text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-50 focus:ring-indigo-500"
              >
                Checkout
              </button>
            </div>
          </section>
        </form>
      </div>
    </div>
  );
}
